import { NextRequest, NextResponse } from 'next/server';

export async function POST(request: NextRequest) {
  try {
    const { code } = await request.json();

    if (!code) {
      return NextResponse.json({ error: 'Authorization code is required' }, { status: 400 });
    }

    const clientId = process.env.NEXT_PUBLIC_DROPBOX_CLIENT_ID;
    const clientSecret = process.env.DROPBOX_CLIENT_SECRET;
    const redirectUri = process.env.NEXT_PUBLIC_DROPBOX_REDIRECT_URI;

    if (!clientId || !clientSecret || !redirectUri) {
      console.error('Missing Dropbox credentials');
      return NextResponse.json({ error: 'Server configuration error' }, { status: 500 });
    }

    // Exchange authorization code for access token
    const tokenResponse = await fetch('https://api.dropboxapi.com/oauth2/token', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/x-www-form-urlencoded',
      },
      body: new URLSearchParams({
        code,
        grant_type: 'authorization_code',
        client_id: clientId,
        client_secret: clientSecret,
        redirect_uri: redirectUri,
      }),
    });

    if (!tokenResponse.ok) {
      const errorData = await tokenResponse.text();
      console.error('Dropbox token exchange failed:', errorData);
      return NextResponse.json({ error: 'Token exchange failed' }, { status: 400 });
    }

    const tokenData = await tokenResponse.json();

    return NextResponse.json(tokenData);
  } catch (error) {
    console.error('Dropbox token exchange error:', error);
    return NextResponse.json({ error: 'Internal server error' }, { status: 500 });
  }
}

import { NextRequest, NextResponse } from 'next/server';

export async function POST(request: NextRequest) {
  console.log(' Dropbox token exchange started');
  
  try {
    const { code } = await request.json();
    console.log(' Authorization code received:', code ? 'Present' : 'Missing');
    
    if (!code) {
      console.error(' No authorization code provided');
      return NextResponse.json({ error: 'Authorization code is required' }, { status: 400 });
    }

    const clientId = process.env.NEXT_PUBLIC_DROPBOX_CLIENT_ID;
    const clientSecret = process.env.DROPBOX_CLIENT_SECRET;
    const redirectUri = process.env.NEXT_PUBLIC_DROPBOX_REDIRECT_URI;

    console.log(' Environment variables check:');
    console.log(' Client ID:', clientId ? 'Present' : 'Missing');
    console.log(' Client Secret:', clientSecret ? 'Present' : 'Missing');
    console.log(' Redirect URI:', redirectUri ? 'Present' : 'Missing');

    if (!clientId || !clientSecret || !redirectUri) {
      console.error(' Missing Dropbox credentials');
      return NextResponse.json({ 
        error: 'Server configuration error - Missing environment variables',
        details: {
          clientId: !!clientId,
          clientSecret: !!clientSecret,
          redirectUri: !!redirectUri
        }
      }, { status: 500 });
    }

    console.log(' Making token exchange request to Dropbox API');
    
    // Exchange authorization code for access token
    const tokenResponse = await fetch('https://api.dropboxapi.com/oauth2/token', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/x-www-form-urlencoded',
      },
      body: new URLSearchParams({
        code,
        grant_type: 'authorization_code',
        client_id: clientId,
        client_secret: clientSecret,
        redirect_uri: redirectUri,
      }),
    });

    console.log(' Dropbox API response status:', tokenResponse.status);

    if (!tokenResponse.ok) {
      const errorData = await tokenResponse.text();
      console.error(' Dropbox token exchange failed:', errorData);
      console.error(' Response status:', tokenResponse.status);
      console.error(' Response headers:', Object.fromEntries(tokenResponse.headers.entries()));
      
      return NextResponse.json({ 
        error: 'Token exchange failed',
        details: errorData,
        status: tokenResponse.status
      }, { status: 400 });
    }

    const tokenData = await tokenResponse.json();
    console.log(' Token exchange successful');
    console.log(' Token data keys:', Object.keys(tokenData));
    
    return NextResponse.json(tokenData);
  } catch (error) {
    console.error(' Dropbox token exchange error:', error);
    return NextResponse.json({ 
      error: 'Internal server error',
      details: error instanceof Error ? error.message : 'Unknown error'
    }, { status: 500 });
  }
}

import { NextRequest, NextResponse } from 'next/server';

export async function POST(request: NextRequest) {
  try {
    const { code } = await request.json();

    if (!code) {
      return NextResponse.json({ error: 'Authorization code is required' }, { status: 400 });
    }

    const clientId = process.env.NEXT_PUBLIC_GOOGLE_CLIENT_ID;
    const clientSecret = process.env.GOOGLE_CLIENT_SECRET;
    const redirectUri = process.env.NEXT_PUBLIC_GOOGLE_REDIRECT_URI;

    if (!clientId || !clientSecret || !redirectUri) {
      return NextResponse.json({ error: 'Missing Google OAuth configuration' }, { status: 500 });
    }

    // Google OAuth2 토큰 교환
    const tokenResponse = await fetch('https://oauth2.googleapis.com/token', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/x-www-form-urlencoded',
      },
      body: new URLSearchParams({
        client_id: clientId,
        client_secret: clientSecret,
        code: code,
        grant_type: 'authorization_code',
        redirect_uri: redirectUri,
      }),
    });

    if (!tokenResponse.ok) {
      const errorText = await tokenResponse.text();
      console.error('Google token exchange error:', errorText);
      return NextResponse.json({ error: 'Failed to exchange authorization code' }, { status: 400 });
    }

    const tokenData = await tokenResponse.json();

    // 사용자 정보 가져오기
    const userResponse = await fetch('https://www.googleapis.com/oauth2/v2/userinfo', {
      headers: {
        'Authorization': `Bearer ${tokenData.access_token}`,
      },
    });

    let userData = { id: 'unknown', name: 'Unknown User' };
    if (userResponse.ok) {
      userData = await userResponse.json();
    }

    return NextResponse.json({
      access_token: tokenData.access_token,
      refresh_token: tokenData.refresh_token,
      expires_in: tokenData.expires_in,
      user_id: userData.id,
      display_name: userData.name,
    });

  } catch (error) {
    console.error('Google auth error:', error);
    return NextResponse.json({ error: 'Internal server error' }, { status: 500 });
  }
}

import { NextRequest, NextResponse } from 'next/server';

export async function POST(request: NextRequest) {
  console.log(' Google Drive token exchange started');
  
  try {
    const { code } = await request.json();
    console.log(' Authorization code received:', code ? 'Present' : 'Missing');
    
    if (!code) {
      console.error(' No authorization code provided');
      return NextResponse.json({ error: 'Authorization code is required' }, { status: 400 });
    }

    const clientId = process.env.NEXT_PUBLIC_GOOGLE_CLIENT_ID;
    const clientSecret = process.env.GOOGLE_CLIENT_SECRET;
    const redirectUri = process.env.NEXT_PUBLIC_GOOGLE_REDIRECT_URI;

    console.log(' Environment variables check:');
    console.log(' Client ID:', clientId ? 'Present' : 'Missing');
    console.log(' Client Secret:', clientSecret ? 'Present' : 'Missing');
    console.log(' Redirect URI:', redirectUri ? 'Present' : 'Missing');

    if (!clientId || !clientSecret || !redirectUri) {
      console.error(' Missing Google OAuth credentials');
      return NextResponse.json({ 
        error: 'Server configuration error - Missing environment variables',
        details: {
          clientId: !!clientId,
          clientSecret: !!clientSecret,
          redirectUri: !!redirectUri
        }
      }, { status: 500 });
    }

    console.log(' Making token exchange request to Google API');

    // Google OAuth2 토큰 교환
    const tokenResponse = await fetch('https://oauth2.googleapis.com/token', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/x-www-form-urlencoded',
      },
      body: new URLSearchParams({
        client_id: clientId,
        client_secret: clientSecret,
        code: code,
        grant_type: 'authorization_code',
        redirect_uri: redirectUri,
      }),
    });

    console.log(' Google API response status:', tokenResponse.status);

    if (!tokenResponse.ok) {
      const errorText = await tokenResponse.text();
      console.error(' Google token exchange failed:', errorText);
      console.error(' Response status:', tokenResponse.status);
      console.error(' Response headers:', Object.fromEntries(tokenResponse.headers.entries()));
      
      return NextResponse.json({ 
        error: 'Failed to exchange authorization code',
        details: errorText,
        status: tokenResponse.status
      }, { status: 400 });
    }

    const tokenData = await tokenResponse.json();
    console.log(' Token exchange successful');
    console.log(' Token data keys:', Object.keys(tokenData));

    console.log(' Fetching user information');

    // 사용자 정보 가져오기
    const userResponse = await fetch('https://www.googleapis.com/oauth2/v2/userinfo', {
      headers: {
        'Authorization': `Bearer ${tokenData.access_token}`,
      },
    });

    let userData = { id: 'unknown', name: 'Unknown User' };
    if (userResponse.ok) {
      userData = await userResponse.json();
      console.log(' User info retrieved successfully');
    } else {
      console.warn(' Failed to retrieve user info, using defaults');
    }

    return NextResponse.json({
      ...tokenData,
      user_id: userData.id,
      display_name: userData.name
    });
  } catch (error) {
    console.error(' Google token exchange error:', error);
    return NextResponse.json({ 
      error: 'Internal server error',
      details: error instanceof Error ? error.message : 'Unknown error'
    }, { status: 500 });
  }
}

"use client";

import { Suspense, useEffect } from 'react';
import { useSearchParams } from 'next/navigation';

function DropboxCallbackContent() {
  const searchParams = useSearchParams();

  useEffect(() => {
    const code = searchParams.get('code');
    const error = searchParams.get('error');

    if (error) {
      // Send error to parent window
      window.opener?.postMessage({
        type: 'DROPBOX_AUTH_ERROR',
        error: error
      }, window.location.origin);
      window.close();
      return;
    }

    if (code) {
      // Exchange code for access token
      exchangeCodeForToken(code);
    }
  }, [searchParams]);

  const exchangeCodeForToken = async (code: string) => {
    try {
      const response = await fetch('/api/auth/dropbox/token', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ code })
      });

      if (!response.ok) {
        throw new Error('Token exchange failed');
      }

      const tokenData = await response.json();

      // Send success data to parent window
      window.opener?.postMessage({
        type: 'DROPBOX_AUTH_SUCCESS',
        accessToken: tokenData.access_token,
        refreshToken: tokenData.refresh_token,
        expiresAt: Date.now() + (tokenData.expires_in * 1000),
        userId: tokenData.account_id,
        displayName: tokenData.account_id // Dropbox doesn't provide display name in token response
      }, window.location.origin);

      window.close();
    } catch (error) {
      console.error('Token exchange error:', error);
      window.opener?.postMessage({
        type: 'DROPBOX_AUTH_ERROR',
        error: 'Token exchange failed'
      }, window.location.origin);
      window.close();
    }
  };

  return (
    <div className="flex min-h-screen items-center justify-center p-8">
      <div className="text-center">
        <h1 className="text-2xl font-bold mb-4">Dropbox 인증 중...</h1>
        <p className="text-zinc-600">인증을 처리하고 있습니다. 잠시만 기다려주세요.</p>
      </div>
    </div>
  );
}

export default function DropboxCallback() {
  return (
    <Suspense fallback={
      <div className="flex min-h-screen items-center justify-center p-8">
        <div className="text-center">
          <h1 className="text-2xl font-bold mb-4">로딩 중...</h1>
          <p className="text-zinc-600">잠시만 기다려주세요.</p>
        </div>
      </div>
    }>
      <DropboxCallbackContent />
    </Suspense>
  );
}

"use client";

import { Suspense, useEffect } from 'react';
import { useSearchParams } from 'next/navigation';

function DropboxCallbackContent() {
  const searchParams = useSearchParams();

  useEffect(() => {
    const code = searchParams.get('code');
    const error = searchParams.get('error');

    // 부모 창의 origin을 가져오기 (더 안전한 방법)
    const getParentOrigin = () => {
      try {
        return window.opener?.location.origin || '*';
      } catch (e) {
        // Cross-origin 접근 불가 시 와일드카드 사용 (보안상 주의)
        return '*';
      }
    };

    if (error) {
      // Send error to parent window
      const targetOrigin = getParentOrigin();
      window.opener?.postMessage({
        type: 'DROPBOX_AUTH_ERROR',
        error: error
      }, targetOrigin);
      window.close();
      return;
    }

    if (code) {
      // Exchange code for access token
      exchangeCodeForToken(code);
    }
  }, [searchParams]);

  const exchangeCodeForToken = async (code: string) => {
    try {
      const response = await fetch('/api/auth/dropbox/token', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ code })
      });

      if (!response.ok) {
        throw new Error('Token exchange failed');
      }

      const tokenData = await response.json();
      
      // 부모 창의 origin을 가져오기
      const getParentOrigin = () => {
        try {
          return window.opener?.location.origin || '*';
        } catch (e) {
          return '*';
        }
      };
      
      const targetOrigin = getParentOrigin();
      
      // Send success data to parent window
      window.opener?.postMessage({
        type: 'DROPBOX_AUTH_SUCCESS',
        accessToken: tokenData.access_token,
        refreshToken: tokenData.refresh_token,
        expiresAt: Date.now() + (tokenData.expires_in * 1000),
        userId: tokenData.account_id,
        displayName: tokenData.account_id // Dropbox doesn't provide display name in token response
      }, targetOrigin);
      
      window.close();
    } catch (error) {
      console.error('Token exchange error:', error);
      
      const getParentOrigin = () => {
        try {
          return window.opener?.location.origin || '*';
        } catch (e) {
          return '*';
        }
      };
      
      const targetOrigin = getParentOrigin();
      
      window.opener?.postMessage({
        type: 'DROPBOX_AUTH_ERROR',
        error: 'Token exchange failed'
      }, targetOrigin);
      window.close();
    }
  };

  return (
    <div className="flex min-h-screen items-center justify-center p-8">
      <div className="text-center">
        <h1 className="text-2xl font-bold mb-4">Dropbox 인증 중...</h1>
        <p className="text-zinc-600">인증을 처리하고 있습니다. 잠시만 기다려주세요.</p>
      </div>
    </div>
  );
}

export default function DropboxCallback() {
  return (
    <Suspense fallback={
      <div className="flex min-h-screen items-center justify-center p-8">
        <div className="text-center">
          <h1 className="text-2xl font-bold mb-4">로딩 중...</h1>
          <p className="text-zinc-600">잠시만 기다려주세요.</p>
        </div>
      </div>
    }>
      <DropboxCallbackContent />
    </Suspense>
  );
}

"use client";

import { useEffect, Suspense } from 'react';
import { useSearchParams } from 'next/navigation';

function GoogleCallbackContent() {
  const searchParams = useSearchParams();

  useEffect(() => {
    const code = searchParams.get('code');
    const error = searchParams.get('error');

    if (error) {
      // OAuth 에러 처리
      window.opener?.postMessage({
        type: 'GOOGLE_AUTH_ERROR',
        error: error
      }, window.location.origin);
      window.close();
      return;
    }

    if (code) {
      // Authorization code를 access token으로 교환
      fetch('/api/auth/google/token', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ code })
      })
      .then(response => response.json())
      .then(data => {
        if (data.error) {
          window.opener?.postMessage({
            type: 'GOOGLE_AUTH_ERROR',
            error: data.error
          }, window.location.origin);
        } else {
          // 성공적으로 토큰을 받았을 때
          window.opener?.postMessage({
            type: 'GOOGLE_AUTH_SUCCESS',
            accessToken: data.access_token,
            refreshToken: data.refresh_token,
            expiresAt: Date.now() + (data.expires_in * 1000),
            userId: data.user_id,
            displayName: data.display_name
          }, window.location.origin);
        }
        window.close();
      })
      .catch(error => {
        console.error('Token exchange error:', error);
        window.opener?.postMessage({
          type: 'GOOGLE_AUTH_ERROR',
          error: 'Token exchange failed'
        }, window.location.origin);
        window.close();
      });
    }
  }, [searchParams]);

  return (
    <div className="flex items-center justify-center min-h-screen">
      <div className="text-center">
        <h2 className="text-lg font-semibold mb-2">Google Drive 인증 중...</h2>
        <p className="text-zinc-600">잠시만 기다려주세요.</p>
      </div>
    </div>
  );
}

export default function GoogleCallbackPage() {
  return (
    <Suspense fallback={
      <div className="flex items-center justify-center min-h-screen">
        <div className="text-center">
          <h2 className="text-lg font-semibold mb-2">로딩 중...</h2>
          <p className="text-zinc-600">잠시만 기다려주세요.</p>
        </div>
      </div>
    }>
      <GoogleCallbackContent />
    </Suspense>
  );
}

