import * as React from "react";
import type { Album } from "@/types/album";
import { Star, Trash2 } from "lucide-react";
import Image from 'next/image';
import { getPrimaryCoverImage } from "@/lib/album-images";

interface AlbumCardProps {
  album: Album;
  onClick?: () => void;
  onDelete?: () => void;
  onEdit?: () => void;
}

export function AlbumCard({ album, onClick, onDelete }: AlbumCardProps) {
  const primaryImageUrl = getPrimaryCoverImage(album);

  return (
    <div
      className="relative bg-white dark:bg-zinc-900 rounded-lg shadow p-4 flex flex-col items-center gap-2 border border-zinc-200 dark:border-zinc-800 cursor-pointer transition-all duration-200 ease-in-out hover:scale-105 hover:shadow-2xl focus:outline-none focus:ring-2 focus:ring-blue-400 hover:opacity-95 min-h-[200px]"
      onClick={onClick}
      tabIndex={0}
      role="button"
      aria-label={`${album.title} 상세 보기`}
      onKeyDown={e => { if (e.key === 'Enter' || e.key === ' ') onClick?.(); }}
    >
      <button
        className="absolute top-2 left-2 text-zinc-400 hover:text-red-500 z-10 bg-white/80 dark:bg-zinc-900/80 rounded-full p-1"
        onClick={e => { e.stopPropagation(); onDelete?.(); }}
        aria-label="앨범 삭제"
        tabIndex={0}
      >
        <Trash2 className="w-5 h-5" />
      </button>
      <div className="w-32 h-32 bg-zinc-100 dark:bg-zinc-800 rounded overflow-hidden flex items-center justify-center">
        {primaryImageUrl ? (
          <Image
            src={primaryImageUrl}
            alt={album.title}
            width={128}
            height={128}
            style={{ objectFit: 'cover' }}
        />
        ) : (
          <span className="text-zinc-400">No Image</span>
        )}
      </div>
      <div className="flex flex-col items-center w-full">
        <span className="font-semibold text-base w-full text-center truncate" title={album.title}>{album.title}</span>
        <span className="text-sm text-zinc-500 w-full text-center truncate" title={album.artist}>{album.artist}</span>
        {album.releaseDate && (
          <span className="text-xs text-zinc-400">{album.releaseDate}</span>
        )}
      </div>
      {album.isFavorite && (
        <Star className="absolute top-2 right-2 text-yellow-400 fill-yellow-400 w-5 h-5" />
      )}
    </div>
  );
}

import * as React from "react";
import type { Album } from "@/types/album";
import { Star, Trash2 } from "lucide-react";
import Image from 'next/image';
import { getPrimaryCoverImage } from "@/lib/album-images";

interface AlbumCardProps {
  album: Album;
  onClick?: () => void;
  onDelete?: () => void;
  onEdit?: () => void;
}

export function AlbumCard({ album, onClick, onDelete }: AlbumCardProps) {
  const primaryImageUrl = getPrimaryCoverImage(album);
  const [imageError, setImageError] = React.useState(false);
  const [imageLoading, setImageLoading] = React.useState(true);

  const handleImageLoad = () => {
    setImageLoading(false);
  };

  const handleImageError = () => {
    setImageError(true);
    setImageLoading(false);
  };

  return (
    <div
      className="relative bg-white dark:bg-zinc-900 rounded-lg shadow p-4 flex flex-col items-center gap-2 border border-zinc-200 dark:border-zinc-800 cursor-pointer transition-all duration-200 ease-in-out hover:scale-105 hover:shadow-2xl focus:outline-none focus:ring-2 focus:ring-blue-400 hover:opacity-95 min-h-[200px]"
      onClick={onClick}
      tabIndex={0}
      role="button"
      aria-label={`${album.title} 상세 보기`}
      onKeyDown={e => { if (e.key === 'Enter' || e.key === ' ') onClick?.(); }}
    >
      <button
        className="absolute top-2 left-2 text-zinc-400 hover:text-red-500 z-10 bg-white/80 dark:bg-zinc-900/80 rounded-full p-1"
        onClick={e => { e.stopPropagation(); onDelete?.(); }}
        aria-label="앨범 삭제"
        tabIndex={0}
      >
        <Trash2 className="w-5 h-5" />
      </button>

      <div className="w-32 h-32 bg-zinc-100 dark:bg-zinc-800 rounded overflow-hidden flex items-center justify-center relative">
        {primaryImageUrl && !imageError ? (
          <>
            {imageLoading && (
              <div className="absolute inset-0 bg-zinc-100 dark:bg-zinc-800 animate-pulse rounded" />
            )}
            <Image
              src={primaryImageUrl}
              alt={album.title}
              width={128}
              height={128}
              className={`transition-opacity duration-300 ${imageLoading ? 'opacity-0' : 'opacity-100'}`}
              style={{ objectFit: 'cover' }}
              onLoad={handleImageLoad}
              onError={handleImageError}
              loading="lazy"
              placeholder="blur"
              blurDataURL="data:image/jpeg;base64,"
            />
          </>
        ) : (
          <span className="text-zinc-400 text-sm">No Image</span>
        )}
      </div>

      <div className="flex flex-col items-center w-full">
        <span className="font-semibold text-base w-full text-center truncate" title={album.title}>{album.title}</span>
        <span className="text-sm text-zinc-500 w-full text-center truncate" title={album.artist}>{album.artist}</span>
        {album.releaseDate && (
          <span className="text-xs text-zinc-400">{album.releaseDate}</span>
        )}
      </div>
      {album.isFavorite && (
        <Star className="absolute top-2 right-2 text-yellow-400 fill-yellow-400 w-5 h-5" />
      )}
    </div>
  );
}

import * as React from "react";
import type { Album } from "@/types/album";
import { AlbumCard } from "@/components/album-card";

interface AlbumGridProps {
  albums: Album[];
  onAlbumClick?: (album: Album) => void;
  onEditAlbum?: (album: Album) => void;
  onDeleteAlbum?: (album: Album) => void;
}

export function AlbumGrid({ albums, onAlbumClick, onEditAlbum, onDeleteAlbum }: AlbumGridProps) {
  if (albums.length === 0) {
    return <div className="text-zinc-400 text-center py-8">등록된 앨범이 없습니다.</div>;
  }
  return (
    <div className="grid grid-cols-1 xs:grid-cols-2 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 xl:grid-cols-5 gap-3 sm:gap-4 md:gap-6 w-full">
      {albums.map((album) => (
        <AlbumCard
          key={album.id}
          album={album}
          onClick={() => onAlbumClick?.(album)}
          onDelete={() => onDeleteAlbum?.(album)}
          onEdit={() => onEditAlbum?.(album)}
        />
      ))}
    </div>
  );
}

import React, { useState } from 'react';
import { useModalAccessibility } from '@/lib/useModalAccessibility';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { X } from 'lucide-react';
import { toast } from 'sonner';
import { createNewFileHandle, setCollectionMetadata, setLastOpenedCollection, setActiveFileHandle } from '@/lib/db';

interface CreateCollectionModalProps {
  onClose: () => void;
  onCollectionCreated: (username: string, collectionName: string) => void;
  initialUsername?: string; // Make it optional
}

export function CreateCollectionModal({
  onClose,
  onCollectionCreated,
  initialUsername = '', // Provide a default value
}: CreateCollectionModalProps) {
  const modalRef = useModalAccessibility(onClose);
  const [username, setUsername] = useState(initialUsername);
  const [collectionName, setCollectionName] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  const handleCreate = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!username.trim() || !collectionName.trim()) {
      toast.error("사용자명과 컬렉션 이름을 입력해주세요.");
      return;
    }

    setIsLoading(true);
    try {
      const fileHandle = await createNewFileHandle(`${collectionName}.json`);
      if (!fileHandle) {
        toast.info("컬렉션 생성이 취소되었습니다.");
        return;
      }

      const initialContent = JSON.stringify({ _metadata: { username: username, collectionName: collectionName }, albums: [] }, null, 2);
      const writable = await fileHandle.createWritable();
      await writable.write(initialContent);
      await writable.close();

      await setCollectionMetadata(username, collectionName, 0);
      await setActiveFileHandle(fileHandle);
      await setLastOpenedCollection(username, collectionName);
      onCollectionCreated(username, collectionName);
    } catch (error) {
      console.error("컬렉션 생성 실패:", error);
      toast.error("컬렉션 생성에 실패했습니다.");
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="fixed inset-0 bg-black/40 flex items-center justify-center z-50 animate-fadein">
      <div
        className="relative bg-white dark:bg-zinc-900 rounded-xl shadow-xl p-6 w-full max-w-md border border-zinc-200 dark:border-zinc-800"
        ref={modalRef}
      >
        <h2 className="text-2xl font-bold mb-4">새 컬렉션 생성</h2>
        <button
          className="absolute top-3 right-3 text-zinc-500 hover:text-zinc-800 dark:hover:text-zinc-200 transition-colors"
          onClick={onClose}
          aria-label="닫기"
        >
          <X className="w-6 h-6" />
        </button>

        <form onSubmit={handleCreate} className="space-y-4">
          <div>
            <Label htmlFor="new-username">사용자명</Label>
            <Input
              id="new-username"
              type="text"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              placeholder="사용자명"
              required
              disabled={isLoading}
            />
          </div>
          <div>
            <Label htmlFor="new-collection-name">컬렉션 이름</Label>
            <Input
              id="new-collection-name"
              type="text"
              value={collectionName}
              onChange={(e) => setCollectionName(e.target.value)}
              placeholder="새 컬렉션 이름"
              required
              disabled={isLoading}
            />
          </div>
          <Button type="submit" className="w-full" disabled={isLoading}>
            {isLoading ? "생성 중..." : "컬렉션 생성"}
          </Button>
        </form>
      </div>
    </div>
  );
}

import React, { useState } from 'react';
import { useModalAccessibility } from '@/lib/useModalAccessibility';
import { useFileOperation } from '@/lib/useAsyncState';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { X } from 'lucide-react';
import { toast } from 'sonner';
import { createNewFileHandle, setCollectionMetadata, setLastOpenedCollection, setActiveFileHandle } from '@/lib/db';

interface CreateCollectionModalProps {
  onClose: () => void;
  onCollectionCreated: (username: string, collectionName: string) => void;
  initialUsername?: string; // Make it optional
}

export function CreateCollectionModal({
  onClose,
  onCollectionCreated,
  initialUsername = '', // Provide a default value
}: CreateCollectionModalProps) {
  const modalRef = useModalAccessibility(onClose);
  const [username, setUsername] = useState(initialUsername);
  const [collectionName, setCollectionName] = useState('');
  const { isLoading, execute } = useFileOperation();

  const handleCreate = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!username.trim() || !collectionName.trim()) {
      toast.error("사용자명과 컬렉션 이름을 입력해주세요.");
      return;
    }

    await execute(async () => {
      const fileHandle = await createNewFileHandle(`${collectionName}.json`);
      if (!fileHandle) {
        toast.info("컬렉션 생성이 취소되었습니다.");
        return { message: "컬렉션 생성이 취소되었습니다." };
      }

      const initialContent = JSON.stringify({ _metadata: { username: username, collectionName: collectionName }, albums: [] }, null, 2);
      const writable = await fileHandle.createWritable();
      await writable.write(initialContent);
      await writable.close();

      await setCollectionMetadata(username, collectionName, 0);
      await setActiveFileHandle(fileHandle);
      await setLastOpenedCollection(username, collectionName);
      onCollectionCreated(username, collectionName);

      return { message: "컬렉션이 성공적으로 생성되었습니다." };
    });
  };

  return (
    <div className="fixed inset-0 bg-black/40 flex items-center justify-center z-50 animate-fadein">
      <div
        className="relative bg-white dark:bg-zinc-900 rounded-xl shadow-xl p-6 w-full max-w-md border border-zinc-200 dark:border-zinc-800"
        ref={modalRef}
      >
        <h2 className="text-2xl font-bold mb-4">새 컬렉션 생성</h2>
        <button
          className="absolute top-3 right-3 text-zinc-500 hover:text-zinc-800 dark:hover:text-zinc-200 transition-colors"
          onClick={onClose}
          aria-label="닫기"
        >
          <X className="w-6 h-6" />
        </button>

        <form onSubmit={handleCreate} className="space-y-4">
          <div>
            <Label htmlFor="new-username">사용자명</Label>
            <Input
              id="new-username"
              type="text"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              placeholder="사용자명"
              required
              disabled={isLoading}
            />
          </div>
          <div>
            <Label htmlFor="new-collection-name">컬렉션 이름</Label>
            <Input
              id="new-collection-name"
              type="text"
              value={collectionName}
              onChange={(e) => setCollectionName(e.target.value)}
              placeholder="새 컬렉션 이름"
              required
              disabled={isLoading}
            />
          </div>
          <Button type="submit" className="w-full" disabled={isLoading}>
            {isLoading ? "생성 중..." : "컬렉션 생성"}
          </Button>
        </form>
      </div>
    </div>
  );
}

"use client";

import React, { Component, ErrorInfo, ReactNode } from 'react';
import { APIError } from '@/lib/error-handler';
import { Button } from '@/components/ui/button';
import { AlertTriangle, RefreshCw, Home } from 'lucide-react';

interface Props {
  children: ReactNode;
  fallback?: ReactNode;
  onError?: (error: Error, errorInfo: ErrorInfo) => void;
}

interface State {
  hasError: boolean;
  error: Error | null;
  errorInfo: ErrorInfo | null;
}

export class ErrorBoundary extends Component<Props, State> {
  constructor(props: Props) {
    super(props);
    this.state = { hasError: false, error: null, errorInfo: null };
  }

  static getDerivedStateFromError(error: Error): State {
    return {
      hasError: true,
      error,
      errorInfo: null,
    };
  }

  componentDidCatch(error: Error, errorInfo: ErrorInfo) {
    console.error('ErrorBoundary caught an error:', error, errorInfo);

    this.setState({
      error,
      errorInfo,
    });

    // 사용자 정의 에러 핸들러 호출
    if (this.props.onError) {
      this.props.onError(error, errorInfo);
    }

    // 에러 로깅 (실제 서비스에서는 에러 리포팅 서비스로 전송)
    this.logErrorToService(error, errorInfo);
  }

  private logErrorToService = (error: Error, errorInfo: ErrorInfo) => {
    // 여기서 실제 에러 리포팅 서비스(Sentry, LogRocket 등)로 전송할 수 있습니다
    console.log('Error logged:', {
      error: error.message,
      stack: error.stack,
      componentStack: errorInfo.componentStack,
      timestamp: new Date().toISOString(),
    });
  };

  private handleReload = () => {
    window.location.reload();
  };

  private handleGoHome = () => {
    window.location.href = '/';
  };

  private handleRetry = () => {
    this.setState({ hasError: false, error: null, errorInfo: null });
  };

  render() {
    if (this.state.hasError) {
      // 사용자 정의 fallback UI가 있으면 사용
      if (this.props.fallback) {
        return this.props.fallback;
      }

      // 기본 에러 UI
      return (
        <div className="min-h-screen flex items-center justify-center bg-gray-50 dark:bg-zinc-900">
          <div className="max-w-md w-full bg-white dark:bg-zinc-800 rounded-lg shadow-lg p-6 border border-gray-200 dark:border-zinc-700">
            <div className="flex items-center justify-center w-12 h-12 mx-auto bg-red-100 dark:bg-red-900/20 rounded-full mb-4">
              <AlertTriangle className="w-6 h-6 text-red-600 dark:text-red-400" />
            </div>

            <h1 className="text-xl font-semibold text-gray-900 dark:text-gray-100 text-center mb-2">
              앗, 문제가 발생했습니다!
            </h1>

            <p className="text-gray-600 dark:text-gray-300 text-center mb-6">
              예상치 못한 오류가 발생했습니다. 페이지를 새로고침하거나 홈으로 돌아가 보세요.
            </p>

            {/* 개발 환경에서만 에러 세부 정보 표시 */}
            {process.env.NODE_ENV === 'development' && this.state.error && (
              <details className="mb-4 p-3 bg-gray-100 dark:bg-zinc-700 rounded text-sm">
                <summary className="cursor-pointer font-medium text-gray-700 dark:text-gray-300">
                  에러 세부 정보 (개발 모드)
                </summary>
                <div className="mt-2 text-red-600 dark:text-red-400 font-mono text-xs whitespace-pre-wrap">
                  {this.state.error.message}
                  {this.state.error.stack && (
                    <div className="mt-2 opacity-75">
                      {this.state.error.stack}
                    </div>
                  )}
                </div>
              </details>
            )}

            <div className="flex flex-col sm:flex-row gap-3">
              <Button
                onClick={this.handleRetry}
                className="flex-1 flex items-center justify-center gap-2"
                variant="outline"
              >
                <RefreshCw className="w-4 h-4" />
                다시 시도
              </Button>

              <Button
                onClick={this.handleReload}
                className="flex-1 flex items-center justify-center gap-2"
                variant="outline"
              >
                <RefreshCw className="w-4 h-4" />
                새로고침
              </Button>

              <Button
                onClick={this.handleGoHome}
                className="flex-1 flex items-center justify-center gap-2"
              >
                <Home className="w-4 h-4" />
                홈으로
              </Button>
            </div>
          </div>
        </div>
      );
    }

    return this.props.children;
  }
}

// 특정 컴포넌트를 위한 간단한 에러 바운더리 HOC
export function withErrorBoundary<P extends object>(
  Component: React.ComponentType<P>,
  fallback?: ReactNode
) {
  const WrappedComponent = (props: P) => (
    <ErrorBoundary fallback={fallback}>
      <Component {...props} />
    </ErrorBoundary>
  );

  WrappedComponent.displayName = `withErrorBoundary(${Component.displayName || Component.name})`;
  return WrappedComponent;
}

// 함수형 컴포넌트를 위한 훅
export function useErrorHandler() {
  return (error: Error, errorInfo?: any) => {
    console.error('Manual error report:', error, errorInfo);

    // 여기서 에러 리포팅 서비스로 전송
    if (typeof window !== 'undefined') {
      // 실제 구현에서는 에러 서비스 API 호출
      console.log('Error reported to service:', {
        error: error.message,
        stack: error.stack,
        userAgent: navigator.userAgent,
        url: window.location.href,
        timestamp: new Date().toISOString(),
        additionalInfo: errorInfo,
      });
    }
  };
}

