"use client";

import React, { useState } from "react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { X } from "lucide-react";
import { toast } from "sonner";
import { loadFromGist, isValidGistUrl, type CollectionData } from "@/services/gist";

interface LoadFromGistModalProps {
  onClose: () => void;
  onLoad: (data: CollectionData) => void;
}

export function LoadFromGistModal({ onClose, onLoad }: LoadFromGistModalProps) {
  const [gistUrl, setGistUrl] = useState("");
  const [isLoading, setIsLoading] = useState(false);

  const handleLoad = async () => {
    if (!gistUrl.trim()) {
      toast.error("Gist URL을 입력해주세요.");
      return;
    }

    if (!isValidGistUrl(gistUrl.trim())) {
      toast.error("올바른 GitHub Gist URL을 입력해주세요.");
      return;
    }

    setIsLoading(true);
    try {
      const data = await loadFromGist(gistUrl.trim());
      onLoad(data);
      toast.success("컬렉션을 성공적으로 불러왔습니다!");
      onClose();
    } catch (error) {
      console.error("Gist 로드 오류:", error);
      toast.error(error instanceof Error ? error.message : "컬렉션을 불러오는데 실패했습니다.");
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="fixed inset-0 bg-black/40 flex items-center justify-center z-50 animate-fadein">
      <div className="relative w-full max-w-md bg-white dark:bg-zinc-900 rounded-xl shadow-lg border border-zinc-200 dark:border-zinc-800 p-6">
        <h2 className="text-xl font-bold mb-4">Gist에서 컬렉션 불러오기</h2>
        <button
          className="absolute top-3 right-3 text-zinc-500 hover:text-zinc-800 dark:hover:text-zinc-200 transition-colors"
          onClick={onClose}
          aria-label="닫기"
        >
          <X className="w-6 h-6" />
        </button>

        <div className="mb-4">
          <Label htmlFor="gist-url" className="block text-sm font-medium text-zinc-700 dark:text-zinc-300 mb-2">
            GitHub Gist URL
          </Label>
          <Input
            id="gist-url"
            type="url"
            value={gistUrl}
            onChange={(e) => setGistUrl(e.target.value)}
            className="w-full px-3 py-2 border border-zinc-300 dark:border-zinc-700 rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500 sm:text-sm bg-white dark:bg-zinc-800 text-zinc-900 dark:text-zinc-100"
            placeholder="https://gist.github.com/username/gist-id"
          />
          <p className="mt-1 text-xs text-zinc-500 dark:text-zinc-400">
            공유받은 GitHub Gist URL을 입력하세요
          </p>
        </div>

        <div className="flex justify-end space-x-2">
          <Button variant="outline" onClick={onClose} disabled={isLoading}>
            취소
          </Button>
          <Button onClick={handleLoad} disabled={isLoading || !gistUrl.trim()}>
            {isLoading ? "불러오는 중..." : "불러오기"}
          </Button>
        </div>
      </div>
    </div>
  );
}

"use client";

import React, { useState } from "react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { X } from "lucide-react";
import { toast } from "sonner";
import { useApiCall } from "@/lib/useAsyncState";
import { loadFromGist, isValidGistUrl, type CollectionData } from "@/services/gist";

interface LoadFromGistModalProps {
  onClose: () => void;
  onLoad: (data: CollectionData) => void;
}

export function LoadFromGistModal({ onClose, onLoad }: LoadFromGistModalProps) {
  const [gistUrl, setGistUrl] = useState("");
  const { isLoading, execute } = useApiCall<CollectionData>();

  const handleLoad = async () => {
    if (!gistUrl.trim()) {
      toast.error("Gist URL을 입력해주세요.");
      return;
    }

    if (!isValidGistUrl(gistUrl.trim())) {
      toast.error("올바른 GitHub Gist URL을 입력해주세요.");
      return;
    }

    await execute(async () => {
      const data = await loadFromGist(gistUrl.trim());
      onLoad(data);
      toast.success("컬렉션을 성공적으로 불러왔습니다!");
      onClose();
      return data;
    });
  };

  return (
    <div className="fixed inset-0 bg-black/40 flex items-center justify-center z-50 animate-fadein">
      <div className="relative w-full max-w-md bg-white dark:bg-zinc-900 rounded-xl shadow-lg border border-zinc-200 dark:border-zinc-800 p-6">
        <h2 className="text-xl font-bold mb-4">Gist에서 컬렉션 불러오기</h2>
        <button
          className="absolute top-3 right-3 text-zinc-500 hover:text-zinc-800 dark:hover:text-zinc-200 transition-colors"
          onClick={onClose}
          aria-label="닫기"
        >
          <X className="w-6 h-6" />
        </button>

        <div className="mb-4">
          <Label htmlFor="gist-url" className="block text-sm font-medium text-zinc-700 dark:text-zinc-300 mb-2">
            GitHub Gist URL
          </Label>
          <Input
            id="gist-url"
            type="url"
            value={gistUrl}
            onChange={(e) => setGistUrl(e.target.value)}
            className="w-full px-3 py-2 border border-zinc-300 dark:border-zinc-700 rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500 sm:text-sm bg-white dark:bg-zinc-800 text-zinc-900 dark:text-zinc-100"
            placeholder="https://gist.github.com/username/gist-id"
          />
          <p className="mt-1 text-xs text-zinc-500 dark:text-zinc-400">
            공유받은 GitHub Gist URL을 입력하세요
          </p>
        </div>

        <div className="flex justify-end space-x-2">
          <Button variant="outline" onClick={onClose} disabled={isLoading}>
            취소
          </Button>
          <Button onClick={handleLoad} disabled={isLoading || !gistUrl.trim()}>
            {isLoading ? "불러오는 중..." : "불러오기"}
          </Button>
        </div>
      </div>
    </div>
  );
}

import * as React from 'react';
import { StorageProvider } from '@/types/storage';
import { storageManager } from '@/lib/storage-manager';
import { Cloud, HardDrive, Loader2, X, Info } from 'lucide-react';

interface StorageConnectionModalProps {
  onClose: () => void;
  onConnectionSelected: (provider: StorageProvider) => void;
}

export function StorageConnectionModal({ onClose, onConnectionSelected }: StorageConnectionModalProps) {
  const modalRef = React.useRef<HTMLDivElement>(null);
  const [isConnecting, setIsConnecting] = React.useState(false);
  const [connectingProvider, setConnectingProvider] = React.useState<StorageProvider | null>(null);

  // 키보드 이벤트 처리
  React.useEffect(() => {
    const handleKeyDown = (e: KeyboardEvent) => {
      if (e.key === 'Escape' && !isConnecting) {
        e.preventDefault();
        e.stopPropagation();
        onClose();
      }

      // Tab 키 트랩핑
      if (e.key === 'Tab' && modalRef.current) {
        const focusable = modalRef.current.querySelectorAll<HTMLElement>(
          'a[href], button:not([disabled]), textarea, input, select, [tabindex]:not([tabindex="-1"])'
        );
        if (focusable.length === 0) return;
        const first = focusable[0];
        const last = focusable[focusable.length - 1];
        if (!e.shiftKey && document.activeElement === last) {
          e.preventDefault();
          first.focus();
        } else if (e.shiftKey && document.activeElement === first) {
          e.preventDefault();
          last.focus();
        }
      }
    };

    document.addEventListener('keydown', handleKeyDown);
    return () => document.removeEventListener('keydown', handleKeyDown);
  }, [onClose, isConnecting]);

  // 오버레이 클릭 처리
  const handleOverlayClick = (e: React.MouseEvent<HTMLDivElement>) => {
    if (e.target === e.currentTarget && !isConnecting) {
      onClose();
    }
  };

  const handleProviderSelect = async (provider: StorageProvider) => {
    if (provider === 'local') {
      onConnectionSelected(provider);
      return;
    }

    try {
      setIsConnecting(true);
      setConnectingProvider(provider);

      await storageManager.connectToProvider(provider);
      onConnectionSelected(provider);
    } catch (error) {
      console.error(`Failed to connect to ${provider}:`, error);

      // Show detailed error message
      const errorMessage = error instanceof Error ? error.message : '알 수 없는 오류가 발생했습니다.';
      alert(`${storageManager.getProviderDisplayName(provider)} 연결 실패:\n\n${errorMessage}`);
    } finally {
      setIsConnecting(false);
      setConnectingProvider(null);
    }
  };

  const handleShowSetupGuide = () => {
    const guideMessage = `클라우드 스토리지 연동 설정 방법:

 Dropbox 설정:
1. https://www.dropbox.com/developers/apps 방문
2. "Create app" 클릭
3. "Scoped access" 선택
4. "App folder" 또는 "Full Dropbox" 선택  
5. 앱 이름 입력
6. App key를 복사하여 NEXT_PUBLIC_DROPBOX_CLIENT_ID에 설정
7. App secret을 복사하여 DROPBOX_CLIENT_SECRET에 설정
8. OAuth2 redirect URL에 다음 추가:
   http://localhost:3000/auth/dropbox/callback

 Google Drive 설정:
1. https://console.developers.google.com 방문
2. 새 프로젝트 생성 또는 선택
3. "APIs & Services" > "Library" 에서 Google Drive API 활성화
4. "Credentials" > "Create Credentials" > "OAuth 2.0 Client IDs"
5. 애플리케이션 유형을 "Web application" 선택
6. 승인된 JavaScript 원본에 http://localhost:3000 추가
7. 클라이언트 ID를 NEXT_PUBLIC_GOOGLE_CLIENT_ID에 설정
8. API 키도 생성하여 NEXT_PUBLIC_GOOGLE_API_KEY에 설정

.env.local 파일에 환경 변수를 설정한 후 개발 서버를 재시작하세요.`;

    alert(guideMessage);
  };

  const providers: Array<{
    id: StorageProvider;
    name: string;
    description: string;
    icon: React.ReactNode;
    color: string;
  }> = [
    {
      id: 'local',
      name: '로컬 파일',
      description: '컴퓨터의 파일을 직접 불러오거나 저장합니다',
      icon: <HardDrive className="h-8 w-8" />,
      color: 'bg-gray-500 hover:bg-gray-600'
    },
    {
      id: 'dropbox',
      name: 'Dropbox',
      description: 'Dropbox 클라우드 스토리지와 연동합니다',
      icon: <Cloud className="h-8 w-8" />,
      color: 'bg-blue-500 hover:bg-blue-600'
    },
    {
      id: 'googledrive',
      name: 'Google Drive',
      description: 'Google Drive 클라우드 스토리지와 연동합니다',
      icon: <Cloud className="h-8 w-8" />,
      color: 'bg-green-500 hover:bg-green-600'
    }
  ];

  return (
    <div 
      className="fixed inset-0 bg-black/60 flex items-center justify-center z-50 p-4"
      onClick={handleOverlayClick}
    >
      <div 
        ref={modalRef}
        className="relative w-full max-w-2xl bg-white dark:bg-zinc-900 rounded-xl shadow-lg border border-zinc-200 dark:border-zinc-800 max-h-[90vh] overflow-hidden"
        onClick={(e) => e.stopPropagation()}
        role="dialog"
        aria-labelledby="storage-connection-title"
        aria-modal="true"
      >
        {/* Header */}
        <div className="p-6 pb-0 border-b border-zinc-200 dark:border-zinc-800">
          <div className="flex items-center justify-between">
            <h2 id="storage-connection-title" className="text-2xl font-bold text-zinc-900 dark:text-zinc-100">
              파일 연동 방식 선택
            </h2>
            <div className="flex items-center gap-2">
              <button
                onClick={onClose}
                disabled={isConnecting}
                className="text-zinc-500 hover:text-zinc-800 dark:hover:text-zinc-200 transition-colors disabled:opacity-50 disabled:cursor-not-allowed"
                aria-label="닫기"
              >
                <X className="w-6 h-6" />
              </button>
            </div>
          </div>
          <p className="text-zinc-600 dark:text-zinc-400 mt-2">
            컬렉션 파일을 저장하고 불러올 방식을 선택해주세요.
          </p>
        </div>

        {/* Content */}
        <div className="p-6">
          <div className="grid gap-4">
            {providers.map((provider) => (
              <button
                key={provider.id}
                onClick={() => handleProviderSelect(provider.id)}
                disabled={isConnecting}
                className="flex items-center gap-4 p-4 border border-zinc-200 dark:border-zinc-700 rounded-lg hover:border-zinc-300 dark:hover:border-zinc-600 transition-colors disabled:opacity-50 disabled:cursor-not-allowed text-left"
              >
                <div className={`p-3 rounded-lg text-white ${provider.color} transition-colors`}>
                  {isConnecting && connectingProvider === provider.id ? (
                    <Loader2 className="h-8 w-8 animate-spin" />
                  ) : (
                    provider.icon
                  )}
                </div>
                <div className="flex-1">
                  <h3 className="text-lg font-semibold text-zinc-900 dark:text-zinc-100">
                    {provider.name}
                  </h3>
                  <p className="text-sm text-zinc-600 dark:text-zinc-400">
                    {provider.description}
                  </p>
                  {isConnecting && connectingProvider === provider.id && (
                    <p className="text-sm text-blue-600 dark:text-blue-400 mt-1">
                      연결 중...
                    </p>
                  )}
                </div>
              </button>
            ))}
          </div>
        </div>

        {/* Loading overlay */}
        {isConnecting && (
          <div className="absolute inset-0 bg-black/20 dark:bg-black/40 flex items-center justify-center rounded-xl backdrop-blur-sm">
            <div className="bg-white dark:bg-zinc-800 p-6 rounded-lg shadow-lg border border-zinc-200 dark:border-zinc-700 flex flex-col items-center gap-4">
              <Loader2 className="h-8 w-8 animate-spin text-blue-600" />
              <div className="text-center">
                <p className="font-medium text-zinc-900 dark:text-zinc-100">
                  {storageManager.getProviderDisplayName(connectingProvider!)}에 연결 중...
                </p>
                <p className="text-sm text-zinc-600 dark:text-zinc-400 mt-1">
                  새 창에서 인증을 완료해주세요
                </p>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

import * as React from 'react';
import { StorageProvider } from '@/types/storage';
import { storageManager } from '@/lib/storage-manager';
import { Cloud, HardDrive, Loader2, X } from 'lucide-react';

interface StorageConnectionModalProps {
  onClose: () => void;
  onConnectionSelected: (provider: StorageProvider) => void;
}

export function StorageConnectionModal({ onClose, onConnectionSelected }: StorageConnectionModalProps) {
  const modalRef = React.useRef<HTMLDivElement>(null);
  const [isConnecting, setIsConnecting] = React.useState(false);
  const [connectingProvider, setConnectingProvider] = React.useState<StorageProvider | null>(null);

  // 키보드 이벤트 처리
  React.useEffect(() => {
    const handleKeyDown = (e: KeyboardEvent) => {
      if (e.key === 'Escape' && !isConnecting) {
        e.preventDefault();
        e.stopPropagation();
        onClose();
      }

      // Tab 키 트랩핑
      if (e.key === 'Tab' && modalRef.current) {
        const focusable = modalRef.current.querySelectorAll<HTMLElement>(
          'a[href], button:not([disabled]), textarea, input, select, [tabindex]:not([tabindex="-1"])'
        );
        if (focusable.length === 0) return;
        const first = focusable[0];
        const last = focusable[focusable.length - 1];
        if (!e.shiftKey && document.activeElement === last) {
          e.preventDefault();
          first.focus();
        } else if (e.shiftKey && document.activeElement === first) {
          e.preventDefault();
          last.focus();
        }
      }
    };

    document.addEventListener('keydown', handleKeyDown);
    return () => document.removeEventListener('keydown', handleKeyDown);
  }, [onClose, isConnecting]);

  // 오버레이 클릭 처리
  const handleOverlayClick = (e: React.MouseEvent<HTMLDivElement>) => {
    if (e.target === e.currentTarget && !isConnecting) {
      onClose();
    }
  };

  const handleProviderSelect = async (provider: StorageProvider) => {
    if (provider === 'local') {
      onConnectionSelected(provider);
      return;
    }

    try {
      setIsConnecting(true);
      setConnectingProvider(provider);

      await storageManager.connectToProvider(provider);
      onConnectionSelected(provider);
    } catch (error) {
      console.error(`Failed to connect to ${provider}:`, error);

      // Show detailed error message
      const errorMessage = error instanceof Error ? error.message : '알 수 없는 오류가 발생했습니다.';
      alert(`연결 실패:\n\n${errorMessage}`);
    } finally {
      setIsConnecting(false);
      setConnectingProvider(null);
    }
  };

  const providers: Array<{
    id: StorageProvider;
    name: string;
    description: string;
    icon: React.ReactNode;
    color: string;
  }> = [
    {
      id: 'local',
      name: '로컬 파일',
      description: '컴퓨터의 파일을 직접 불러오거나 저장합니다',
      icon: <HardDrive className="h-8 w-8" />,
      color: 'bg-gray-500 hover:bg-gray-600'
    },
    {
      id: 'dropbox',
      name: 'Dropbox',
      description: 'Dropbox 클라우드 스토리지와 연동합니다',
      icon: <Cloud className="h-8 w-8" />,
      color: 'bg-blue-500 hover:bg-blue-600'
    },
    {
      id: 'googledrive',
      name: 'Google Drive',
      description: 'Google Drive 클라우드 스토리지와 연동합니다',
      icon: <Cloud className="h-8 w-8" />,
      color: 'bg-green-500 hover:bg-green-600'
    }
  ];

  return (
    <div 
      className="fixed inset-0 bg-black/60 flex items-center justify-center z-50 p-4"
      onClick={handleOverlayClick}
    >
      <div 
        ref={modalRef}
        className="relative w-full max-w-2xl bg-white dark:bg-zinc-900 rounded-xl shadow-lg border border-zinc-200 dark:border-zinc-800 max-h-[90vh] overflow-hidden"
        onClick={(e) => e.stopPropagation()}
        role="dialog"
        aria-labelledby="storage-connection-title"
        aria-modal="true"
      >
        {/* Header */}
        <div className="p-6 pb-0 border-b border-zinc-200 dark:border-zinc-800">
          <div className="flex items-center justify-between">
            <h2 id="storage-connection-title" className="text-2xl font-bold text-zinc-900 dark:text-zinc-100">
              파일 연동 방식 선택
            </h2>
            <div className="flex items-center gap-2">
              <button
                onClick={onClose}
                disabled={isConnecting}
                className="text-zinc-500 hover:text-zinc-800 dark:hover:text-zinc-200 transition-colors disabled:opacity-50 disabled:cursor-not-allowed"
                aria-label="닫기"
              >
                <X className="w-6 h-6" />
              </button>
            </div>
          </div>
          <p className="text-zinc-600 dark:text-zinc-400 mt-2">
            컬렉션 파일을 저장하고 불러올 방식을 선택해주세요.
          </p>
        </div>

        {/* Content */}
        <div className="p-6">
          <div className="grid gap-4">
            {providers.map((provider) => (
              <button
                key={provider.id}
                onClick={() => handleProviderSelect(provider.id)}
                disabled={isConnecting}
                className="flex items-center gap-4 p-4 border border-zinc-200 dark:border-zinc-700 rounded-lg hover:border-zinc-300 dark:hover:border-zinc-600 transition-colors disabled:opacity-50 disabled:cursor-not-allowed text-left"
              >
                <div className={`p-3 rounded-lg text-white ${provider.color} transition-colors`}>
                  {isConnecting && connectingProvider === provider.id ? (
                    <Loader2 className="h-8 w-8 animate-spin" />
                  ) : (
                    provider.icon
                  )}
                </div>
                <div className="flex-1">
                  <h3 className="text-lg font-semibold text-zinc-900 dark:text-zinc-100">
                    {provider.name}
                  </h3>
                  <p className="text-sm text-zinc-600 dark:text-zinc-400">
                    {provider.description}
                  </p>
                  {isConnecting && connectingProvider === provider.id && (
                    <p className="text-sm text-blue-600 dark:text-blue-400 mt-1">
                      연결 중...
                    </p>
                  )}
                </div>
              </button>
            ))}
          </div>
        </div>

        {/* Loading overlay */}
        {isConnecting && (
          <div className="absolute inset-0 bg-black/20 dark:bg-black/40 flex items-center justify-center rounded-xl backdrop-blur-sm">
            <div className="bg-white dark:bg-zinc-800 p-6 rounded-lg shadow-lg border border-zinc-200 dark:border-zinc-700 flex flex-col items-center gap-4">
              <Loader2 className="h-8 w-8 animate-spin text-blue-600" />
              <div className="text-center">
                <p className="font-medium text-zinc-900 dark:text-zinc-100">
                  {storageManager.getProviderDisplayName(connectingProvider!)}에 연결 중...
                </p>
                <p className="text-sm text-zinc-600 dark:text-zinc-400 mt-1">
                  새 창에서 인증을 완료해주세요
                </p>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

import * as React from "react";
import type { Album } from "@/types/album";
import { AlbumCard } from "@/components/album-card";

interface VirtualizedAlbumGridProps {
  albums: Album[];
  onAlbumClick?: (album: Album) => void;
  onEditAlbum?: (album: Album) => void;
  onDeleteAlbum?: (album: Album) => void;
  itemsPerPage?: number;
}

/**
 * 대용량 앨범 컬렉션을 위한 가상화된 그리드 컴포넌트
 * 한 번에 일정 수의 앨범만 렌더링하여 성능을 최적화
 */
export function VirtualizedAlbumGrid({
  albums,
  onAlbumClick,
  onEditAlbum,
  onDeleteAlbum,
  itemsPerPage = 50 // 한 번에 50개씩 렌더링
}: VirtualizedAlbumGridProps) {
  const [currentPage, setCurrentPage] = React.useState(0);
  const [loadedPages, setLoadedPages] = React.useState<Set<number>>(new Set([0]));
  const containerRef = React.useRef<HTMLDivElement>(null);
  const loadingRef = React.useRef<HTMLDivElement>(null);

  // 현재까지 로드된 앨범들
  const totalPages = Math.ceil(albums.length / itemsPerPage);
  const maxLoadedPage = Math.max(...Array.from(loadedPages));
  const visibleAlbums = albums.slice(0, (maxLoadedPage + 1) * itemsPerPage);

  // Intersection Observer를 사용한 무한 스크롤
  React.useEffect(() => {
    if (!loadingRef.current) return;

    const observer = new IntersectionObserver(
      (entries) => {
        const target = entries[0];
        if (target.isIntersecting && maxLoadedPage < totalPages - 1) {
          const nextPage = maxLoadedPage + 1;
          setLoadedPages(prev => new Set([...prev, nextPage]));
          setCurrentPage(nextPage);
        }
      },
      {
        root: null,
        rootMargin: '100px', // 100px 전에 미리 로드
        threshold: 0.1,
      }
    );

    observer.observe(loadingRef.current);

    return () => {
      observer.disconnect();
    };
  }, [maxLoadedPage, totalPages]);

  // 스크롤 위치 복원
  React.useEffect(() => {
    const container = containerRef.current;
    if (!container) return;

    const handleScroll = () => {
      // 스크롤 위치를 sessionStorage에 저장
      sessionStorage.setItem('albumGridScrollTop', container.scrollTop.toString());
    };

    container.addEventListener('scroll', handleScroll);

    // 컴포넌트 마운트 시 스크롤 위치 복원
    const savedScrollTop = sessionStorage.getItem('albumGridScrollTop');
    if (savedScrollTop) {
      container.scrollTop = parseInt(savedScrollTop, 10);
    }

    return () => {
      container.removeEventListener('scroll', handleScroll);
    };
  }, []);

  if (albums.length === 0) {
    return <div className="text-zinc-400 text-center py-8">등록된 앨범이 없습니다.</div>;
  }

  return (
    <div
      ref={containerRef}
      className="max-h-[80vh] overflow-y-auto"
    >
      <div className="grid grid-cols-1 xs:grid-cols-2 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 xl:grid-cols-5 gap-3 sm:gap-4 md:gap-6 w-full">
        {visibleAlbums.map((album) => (
          <AlbumCard
            key={album.id}
            album={album}
            onClick={() => onAlbumClick?.(album)}
            onDelete={() => onDeleteAlbum?.(album)}
            onEdit={() => onEditAlbum?.(album)}
          />
        ))}
      </div>

      {/* 로딩 트리거 및 상태 표시 */}
      {maxLoadedPage < totalPages - 1 && (
        <div
          ref={loadingRef}
          className="flex justify-center items-center py-8"
        >
          <div className="text-zinc-500 text-sm">
            더 많은 앨범 로딩 중... ({visibleAlbums.length}/{albums.length})
          </div>
        </div>
      )}

      {/* 모든 앨범이 로드된 경우 */}
      {maxLoadedPage >= totalPages - 1 && albums.length > itemsPerPage && (
        <div className="text-center py-4 text-zinc-500 text-sm">
          총 {albums.length}개의 앨범을 모두 표시했습니다.
        </div>
      )}
    </div>
  );
}

/**
 * 기존 AlbumGrid와의 호환성을 위한 래퍼 컴포넌트
 */
export function AlbumGrid({ albums, onAlbumClick, onEditAlbum, onDeleteAlbum }: VirtualizedAlbumGridProps) {
  // 앨범 수가 적으면 기존 방식, 많으면 가상화 사용
  const shouldUseVirtualization = albums.length > 100;

  if (!shouldUseVirtualization) {
    // 기존 방식 (100개 이하)
    if (albums.length === 0) {
      return <div className="text-zinc-400 text-center py-8">등록된 앨범이 없습니다.</div>;
    }

    return (
      <div className="grid grid-cols-1 xs:grid-cols-2 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 xl:grid-cols-5 gap-3 sm:gap-4 md:gap-6 w-full">
        {albums.map((album) => (
          <AlbumCard
            key={album.id}
            album={album}
            onClick={() => onAlbumClick?.(album)}
            onDelete={() => onDeleteAlbum?.(album)}
            onEdit={() => onEditAlbum?.(album)}
          />
        ))}
      </div>
    );
  }

  // 가상화 방식 (100개 초과)
  return (
    <VirtualizedAlbumGrid
      albums={albums}
      onAlbumClick={onAlbumClick}
      onEditAlbum={onEditAlbum}
      onDeleteAlbum={onDeleteAlbum}
    />
  );
}

