/**
 * 기존 AlbumGrid와의 호환성을 위한 래퍼 컴포넌트
 */
export function AlbumGrid({ albums, onAlbumClick, onEditAlbum, onDeleteAlbum }: VirtualizedAlbumGridProps) {
  // 앨범 수가 적으면 기존 방식, 많으면 가상화 사용
  const shouldUseVirtualization = albums.length > 100;

  if (!shouldUseVirtualization) {
    // 기존 방식 (100개 이하)
    if (albums.length === 0) {
      return <div className="text-zinc-400 text-center py-8">등록된 앨범이 없습니다.</div>;
    }

    return (
      <div className="grid grid-cols-1 xs:grid-cols-2 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 xl:grid-cols-5 gap-3 sm:gap-4 md:gap-6 w-full">
        {albums.map((album) => (
          <AlbumCard
            key={album.id}
            album={album}
            onClick={() => onAlbumClick?.(album)}
            onDelete={() => onDeleteAlbum?.(album)}
            onEdit={() => onEditAlbum?.(album)}
          />
        ))}
      </div>
    );
  }

  // 가상화 방식 (100개 초과)
  return (
    <VirtualizedAlbumGrid
      albums={albums}
      onAlbumClick={onAlbumClick}
      onEditAlbum={onEditAlbum}
      onDeleteAlbum={onDeleteAlbum}
    />
  );
}

export class APIError extends Error {
  constructor(
    message: string,
    public status?: number,
    public service?: string,
    public originalError?: unknown
  ) {
    super(message);
    this.name = 'APIError';
  }
}

export class AuthenticationError extends APIError {
  constructor(service?: string) {
    super(`${service ? `${service} ` : ''}인증이 필요합니다.`, 401, service);
    this.name = 'AuthenticationError';
  }
}

export class NetworkError extends APIError {
  constructor(service?: string, status?: number, originalError?: unknown) {
    super(`${service ? `${service} ` : ''}네트워크 오류가 발생했습니다.`, status, service, originalError);
    this.name = 'NetworkError';
  }
}

export class ValidationError extends APIError {
  constructor(message: string, service?: string) {
    super(message, 400, service);
    this.name = 'ValidationError';
  }
}

export interface ErrorHandlerOptions {
  showToast?: boolean;
  fallbackMessage?: string;
  logError?: boolean;
}

/**
 * API 에러를 일관된 방식으로 처리하는 유틸리티 함수
 */
export const handleAPIError = (
  error: unknown, 
  service: string,
  options: ErrorHandlerOptions = {}
): APIError => {
  const { fallbackMessage = '알 수 없는 오류가 발생했습니다.', logError = true } = options;

  if (logError) {
    console.error(`[${service}] Error:`, error);
  }

  // 이미 APIError 인스턴스인 경우
  if (error instanceof APIError) {
    return error;
  }

  // Response 객체인 경우
  if (error instanceof Response) {
    if (error.status === 401) {
      return new AuthenticationError(service);
    }
    return new NetworkError(service, error.status);
  }

  // 일반 Error 객체인 경우
  if (error instanceof Error) {
    if (error.message.includes('Not authenticated') || error.message.includes('Authentication')) {
      return new AuthenticationError(service);
    }
    if (error.message.includes('Failed to') || error.message.includes('Network')) {
      return new NetworkError(service, undefined, error);
    }
    return new APIError(error.message, undefined, service, error);
  }

  // 기타 경우
  return new APIError(fallbackMessage, undefined, service, error);
};

/**
 * 환경 변수 누락 에러를 처리하는 유틸리티 함수
 */
export const createEnvironmentError = (service: string, requiredVars: string[]): APIError => {
  const message = `${service} 연동을 위해서는 환경 변수 설정이 필요합니다.

필요한 환경 변수:
${requiredVars.map(v => `- ${v}`).join('\n')}

.env.local 파일에 해당 값들을 설정해주세요.`;

  return new ValidationError(message, service);
};

/**
 * 사용자 친화적인 에러 메시지를 생성하는 함수
 */
export const getUserFriendlyErrorMessage = (error: APIError): string => {
  if (error instanceof AuthenticationError) {
    return `${error.service || '서비스'} 로그인이 필요합니다. 다시 로그인해주세요.`;
  }

  if (error instanceof NetworkError) {
    return `${error.service || '서비스'} 연결에 문제가 발생했습니다. 인터넷 연결을 확인하고 다시 시도해주세요.`;
  }

  if (error instanceof ValidationError) {
    return error.message;
  }

  return error.message || '알 수 없는 오류가 발생했습니다.';
};

import { StorageService, StorageProvider, StorageConnection } from '@/types/storage';
import { DropboxService } from '@/services/dropbox';
import { GoogleDriveService } from '@/services/google-drive';

export class StorageManager {
  private services: Map<StorageProvider, StorageService> = new Map();
  private activeConnection: StorageConnection | null = null;

  constructor() {
    this.services.set('dropbox', new DropboxService());
    this.services.set('googledrive', new GoogleDriveService());
  }

  getService(provider: StorageProvider): StorageService | undefined {
    return this.services.get(provider);
  }

  async connectToProvider(provider: StorageProvider): Promise<StorageConnection> {
    const service = this.getService(provider);
    if (!service) {
      throw new Error(`Unsupported provider: ${provider}`);
    }

    console.log(' Connecting to provider:', provider);
    const connection = await service.authenticate();
    console.log(' Connection established:', connection);

    this.activeConnection = connection;

    // Store connection in localStorage for persistence
    localStorage.setItem('storage-connection', JSON.stringify(connection));

    // Set access token for Dropbox service
    if (provider === 'dropbox' && service instanceof DropboxService) {
      (service as { accessToken?: string }).accessToken = connection.accessToken;
    }

    return connection;
  }

  getActiveConnection(): StorageConnection | null {
    if (this.activeConnection) {
      return this.activeConnection;
    }

    // Try to restore from localStorage
    const stored = localStorage.getItem('storage-connection');
    if (stored) {
      try {
        this.activeConnection = JSON.parse(stored);
        return this.activeConnection;
      } catch (e) {
        console.error('Failed to parse stored connection:', e);
        localStorage.removeItem('storage-connection');
      }
    }

    return null;
  }

  disconnect(): void {
    this.activeConnection = null;
    localStorage.removeItem('storage-connection');
  }

  isConnected(): boolean {
    const connection = this.getActiveConnection();
    if (!connection) return false;

    // Check if token is expired
    if (connection.expiresAt && Date.now() > connection.expiresAt) {
      this.disconnect();
      return false;
    }

    return true;
  }

  getProviderDisplayName(provider: StorageProvider): string {
    switch (provider) {
      case 'dropbox':
        return 'Dropbox';
      case 'googledrive':
        return 'Google Drive';
      case 'onedrive':
        return 'OneDrive';
      case 'local':
        return '로컬 파일';
      default:
        return provider;
    }
  }
}

// Singleton instance
export const storageManager = new StorageManager();

