import { useState, useCallback } from 'react';
import { APIError, getUserFriendlyErrorMessage } from './error-handler';
import { toast } from 'sonner';

export interface AsyncState<T> {
  data: T | null;
  isLoading: boolean;
  error: string | null;
}

export interface UseAsyncStateOptions {
  showToast?: boolean;
  initialData?: any;
  onSuccess?: (data: any) => void;
  onError?: (error: APIError) => void;
}

/**
 * 비동기 작업의 로딩, 성공, 실패 상태를 관리하는 커스텀 훅
 */
export function useAsyncState<T = any>(options: UseAsyncStateOptions = {}) {
  const { showToast = true, initialData = null, onSuccess, onError } = options;

  const [state, setState] = useState<AsyncState<T>>({
    data: initialData,
    isLoading: false,
    error: null,
  });

  const execute = useCallback(async (asyncFunction: () => Promise<T>) => {
    setState(prev => ({ ...prev, isLoading: true, error: null }));

    try {
      const result = await asyncFunction();
      setState({
        data: result,
        isLoading: false,
        error: null,
      });

      if (onSuccess) {
        onSuccess(result);
      }

      return result;
    } catch (error) {
      const apiError = error instanceof APIError ? error : new APIError('알 수 없는 오류가 발생했습니다.');
      const errorMessage = getUserFriendlyErrorMessage(apiError);

      setState(prev => ({
        ...prev,
        isLoading: false,
        error: errorMessage,
      }));

      if (showToast) {
        toast.error(errorMessage);
      }

      if (onError) {
        onError(apiError);
      }

      throw apiError;
    }
  }, [showToast, onSuccess, onError]);

  const reset = useCallback(() => {
    setState({
      data: initialData,
      isLoading: false,
      error: null,
    });
  }, [initialData]);

  const setData = useCallback((data: T) => {
    setState(prev => ({ ...prev, data }));
  }, []);

  const setError = useCallback((error: string) => {
    setState(prev => ({ ...prev, error, isLoading: false }));
  }, []);

  return {
    ...state,
    execute,
    reset,
    setData,
    setError,
  };
}

/**
 * 파일 업로드/다운로드 등의 작업을 위한 특화된 훅
 */
export function useFileOperation() {
  return useAsyncState({
    showToast: true,
    onSuccess: (data) => {
      if (data && typeof data === 'object' && 'message' in data) {
        toast.success(data.message as string);
      }
    },
  });
}

/**
 * API 호출을 위한 특화된 훅
 */
export function useApiCall<T = any>(options: UseAsyncStateOptions = {}) {
  return useAsyncState<T>({
    showToast: true,
    ...options,
  });
}

import { useState, useCallback } from 'react';
import { APIError, getUserFriendlyErrorMessage } from './error-handler';
import { toast } from 'sonner';

export interface AsyncState<T> {
  data: T | null;
  isLoading: boolean;
  error: string | null;
}

export interface UseAsyncStateOptions<T = unknown> {
  showToast?: boolean;
  initialData?: T;
  onSuccess?: (data: T) => void;
  onError?: (error: APIError) => void;
}

/**
 * 비동기 작업의 로딩, 성공, 실패 상태를 관리하는 커스텀 훅
 */
export function useAsyncState<T = unknown>(options: UseAsyncStateOptions<T> = {}) {
  const { showToast = true, initialData = null, onSuccess, onError } = options;

  const [state, setState] = useState<AsyncState<T>>({
    data: initialData,
    isLoading: false,
    error: null,
  });

  const execute = useCallback(async (asyncFunction: () => Promise<T>) => {
    setState(prev => ({ ...prev, isLoading: true, error: null }));

    try {
      const result = await asyncFunction();
      setState({
        data: result,
        isLoading: false,
        error: null,
      });

      if (onSuccess) {
        onSuccess(result);
      }

      return result;
    } catch (error) {
      const apiError = error instanceof APIError ? error : new APIError('알 수 없는 오류가 발생했습니다.');
      const errorMessage = getUserFriendlyErrorMessage(apiError);

      setState(prev => ({
        ...prev,
        isLoading: false,
        error: errorMessage,
      }));

      if (showToast) {
        toast.error(errorMessage);
      }

      if (onError) {
        onError(apiError);
      }

      throw apiError;
    }
  }, [showToast, onSuccess, onError]);

  const reset = useCallback(() => {
    setState({
      data: initialData,
      isLoading: false,
      error: null,
    });
  }, [initialData]);

  const setData = useCallback((data: T) => {
    setState(prev => ({ ...prev, data }));
  }, []);

  const setError = useCallback((error: string) => {
    setState(prev => ({ ...prev, error, isLoading: false }));
  }, []);

  return {
    ...state,
    execute,
    reset,
    setData,
    setError,
  };
}

/**
 * 파일 업로드/다운로드 등의 작업을 위한 특화된 훅
 */
export function useFileOperation() {
  return useAsyncState({
    showToast: true,
    onSuccess: (data) => {
      if (data && typeof data === 'object' && 'message' in data) {
        toast.success(data.message as string);
      }
    },
  });
}

/**
 * API 호출을 위한 특화된 훅
 */
export function useApiCall<T = unknown>(options: UseAsyncStateOptions<T> = {}) {
  return useAsyncState<T>({
    showToast: true,
    ...options,
  });
}

import { StorageService, StorageConnection, CloudFile, StorageProvider } from '@/types/storage';
import { handleAPIError, createEnvironmentError, AuthenticationError, NetworkError } from '@/lib/error-handler';

interface DropboxEntry {
  '.tag': string;
  id: string;
  name: string;
  path_display: string;
  size: number;
  client_modified: string;
}

export class DropboxService implements StorageService {
  public accessToken: string | null = null;
  private readonly clientId = process.env.NEXT_PUBLIC_DROPBOX_CLIENT_ID;
  private readonly redirectUri = process.env.NEXT_PUBLIC_DROPBOX_REDIRECT_URI;

  async authenticate(): Promise<StorageConnection> {
    if (!this.clientId || !this.redirectUri) {
      throw createEnvironmentError('Dropbox', [
        'NEXT_PUBLIC_DROPBOX_CLIENT_ID',
        'NEXT_PUBLIC_DROPBOX_REDIRECT_URI'
      ]);
    }

    // OAuth 2.0 flow
    const authUrl = `https://www.dropbox.com/oauth2/authorize?client_id=${this.clientId}&redirect_uri=${encodeURIComponent(this.redirectUri)}&response_type=code&token_access_type=offline`;

    // Open popup window for authentication
    const popup = window.open(authUrl, 'dropbox-auth', 'width=500,height=600');

    return new Promise((resolve, reject) => {
      const checkClosed = setInterval(() => {
        if (popup?.closed) {
          clearInterval(checkClosed);
          reject(new Error('Authentication cancelled'));
        }
      }, 1000);

      // Listen for message from popup
      const messageHandler = (event: MessageEvent) => {
        if (event.origin !== window.location.origin) return;

        if (event.data.type === 'DROPBOX_AUTH_SUCCESS') {
          clearInterval(checkClosed);
          popup?.close();
          window.removeEventListener('message', messageHandler);

          this.accessToken = event.data.accessToken;
          resolve({
            provider: 'dropbox',
            accessToken: event.data.accessToken,
            refreshToken: event.data.refreshToken,
            expiresAt: event.data.expiresAt,
            userId: event.data.userId,
            displayName: event.data.displayName
          });
        } else if (event.data.type === 'DROPBOX_AUTH_ERROR') {
          clearInterval(checkClosed);
          popup?.close();
          window.removeEventListener('message', messageHandler);
          reject(new Error(event.data.error));
        }
      };

      window.addEventListener('message', messageHandler);
    });
  }

  async listFiles(path: string = ''): Promise<CloudFile[]> {
    if (!this.accessToken) {
      throw new AuthenticationError('Dropbox');
    }

    console.log(' Dropbox listFiles called with path:', path, 'token:', this.accessToken ? 'Token exists' : 'No token');

    try {
      const response = await fetch('/api/dropbox/proxy', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          action: 'list_files',
          data: { path },
          accessToken: this.accessToken
        })
      });

      console.log(' Dropbox API response status:', response.status);

      if (!response.ok) {
        const errorText = await response.text();
        console.error(' Dropbox API error:', errorText);
        throw new NetworkError('Dropbox', response.status);
      }

      const data = await response.json();
      console.log(' Dropbox API response data:', data);
      console.log(' Total entries found:', data.entries?.length || 0);

      if (data.entries) {
        const allFiles = data.entries.filter((entry: DropboxEntry) => entry['.tag'] === 'file');
        const jsonFiles = allFiles.filter((entry: DropboxEntry) => entry.name.endsWith('.json'));

        console.log(' All files:', allFiles.map((f: DropboxEntry) => f.name));
        console.log(' JSON files:', jsonFiles.map((f: DropboxEntry) => f.name));

        // 폴더도 보여주기 (디버깅용)
        const folders = data.entries.filter((entry: DropboxEntry) => entry['.tag'] === 'folder');
        console.log(' Folders found:', folders.map((f: DropboxEntry) => f.name));

        return jsonFiles.map((entry: DropboxEntry) => ({
          id: entry.path_display, // Use path_display as ID for downloads
          name: entry.name,
          path: entry.path_display,
          size: entry.size,
          modifiedAt: entry.client_modified,
          provider: 'dropbox' as StorageProvider
        }));
      }

      console.log(' No entries found in response');
      return [];
    } catch (error) {
      console.error(' Dropbox listFiles error:', error);
      throw handleAPIError(error, 'Dropbox');
    }
  }

  async getFile(fileId: string): Promise<string> {
    if (!this.accessToken) {
      throw new AuthenticationError('Dropbox');
    }

    try {
      const response = await fetch('/api/dropbox/proxy', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          action: 'download_file',
          data: { path: fileId },
          accessToken: this.accessToken
        })
      });

      if (!response.ok) {
        throw new NetworkError('Dropbox', response.status);
      }

      const result = await response.json();
      return result.content;
    } catch (error) {
      throw handleAPIError(error, 'Dropbox');
    }
  }

  async saveFile(fileId: string, content: string): Promise<void> {
    if (!this.accessToken) {
      throw new AuthenticationError('Dropbox');
    }

    try {
      const response = await fetch('/api/dropbox/proxy', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          action: 'upload_file',
          data: {
            path: fileId,
            content: content,
            mode: 'overwrite'
          },
          accessToken: this.accessToken
        })
      });

      if (!response.ok) {
        throw new NetworkError('Dropbox', response.status);
      }
    } catch (error) {
      throw handleAPIError(error, 'Dropbox');
    }
  }

  async createFile(name: string, content: string, path: string = ''): Promise<CloudFile> {
    if (!this.accessToken) {
      throw new AuthenticationError('Dropbox');
    }

    try {
      const filePath = path ? `${path}/${name}` : `/${name}`;

      const response = await fetch('/api/dropbox/proxy', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          action: 'upload_file',
          data: {
            path: filePath,
            content: content,
            mode: 'add'
          },
          accessToken: this.accessToken
        })
      });

      if (!response.ok) {
        throw new NetworkError('Dropbox', response.status);
      }

      const data = await response.json();
      return {
        id: data.path_display,
        name: data.name,
        path: data.path_display,
        size: data.size,
        modifiedAt: data.client_modified,
        provider: 'dropbox'
      };
    } catch (error) {
      throw handleAPIError(error, 'Dropbox');
    }
  }

  async deleteFile(fileId: string): Promise<void> {
    if (!this.accessToken) {
      throw new AuthenticationError('Dropbox');
    }

    try {
      const response = await fetch('/api/dropbox/proxy', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          action: 'delete_file',
          data: { path: fileId },
          accessToken: this.accessToken
        })
      });

      if (!response.ok) {
        throw new NetworkError('Dropbox', response.status);
      }
    } catch (error) {
      throw handleAPIError(error, 'Dropbox');
    }
  }
}

import { StorageService, StorageConnection, CloudFile, StorageProvider } from '@/types/storage';
import { handleAPIError, createEnvironmentError, AuthenticationError, NetworkError } from '@/lib/error-handler';

interface DropboxEntry {
  '.tag': string;
  id: string;
  name: string;
  path_display: string;
  size: number;
  client_modified: string;
}

export class DropboxService implements StorageService {
  public accessToken: string | null = null;
  private readonly clientId = process.env.NEXT_PUBLIC_DROPBOX_CLIENT_ID;
  private readonly redirectUri = process.env.NEXT_PUBLIC_DROPBOX_REDIRECT_URI;

  async authenticate(): Promise<StorageConnection> {
    console.log(' Dropbox authenticate started');
    console.log(' Client ID:', this.clientId ? 'Present' : 'Missing');
    console.log(' Redirect URI:', this.redirectUri ? 'Present' : 'Missing');

    if (!this.clientId || !this.redirectUri) {
      const error = createEnvironmentError('Dropbox', [
        'NEXT_PUBLIC_DROPBOX_CLIENT_ID',
        'NEXT_PUBLIC_DROPBOX_REDIRECT_URI'
      ]);
      console.error(' Environment error:', error.message);
      throw error;
    }

    try {
      // OAuth 2.0 flow
      const authUrl = `https://www.dropbox.com/oauth2/authorize?client_id=${this.clientId}&redirect_uri=${encodeURIComponent(this.redirectUri)}&response_type=code&token_access_type=offline`;
      console.log(' Opening popup with URL:', authUrl);
      
      // Open popup window for authentication
      const popup = window.open(authUrl, 'dropbox-auth', 'width=500,height=600');
      
      if (!popup) {
        throw new Error('팝업이 차단되었습니다. 팝업 차단을 해제하고 다시 시도해주세요.');
      }

      console.log(' Popup opened, waiting for callback...');
      
      return new Promise((resolve, reject) => {
        let isResolved = false;

        const checkClosed = setInterval(() => {
          if (popup?.closed && !isResolved) {
            console.log(' Popup was closed by user');
            clearInterval(checkClosed);
            isResolved = true;
            reject(new Error('사용자가 인증을 취소했습니다.'));
          }
        }, 1000);

        // Listen for message from popup
        const messageHandler = (event: MessageEvent) => {
          console.log(' Received message:', event);
          console.log(' Message origin:', event.origin);
          console.log(' Current origin:', window.location.origin);
          
          // Origin check - be more flexible for development
          const validOrigins = [
            window.location.origin,
            'http://localhost:3001',
            'http://127.0.0.1:3001',
            'http://10.5.0.2:3001' // Add Docker/network origin
          ];
          
          if (!validOrigins.includes(event.origin)) {
            console.warn(' Message from invalid origin:', event.origin);
            return;
          }
          
          if (event.data.type === 'DROPBOX_AUTH_SUCCESS') {
            console.log(' Authentication successful');
            clearInterval(checkClosed);
            popup?.close();
            window.removeEventListener('message', messageHandler);
            isResolved = true;
            
            this.accessToken = event.data.accessToken;
            resolve({
              provider: 'dropbox',
              accessToken: event.data.accessToken,
              refreshToken: event.data.refreshToken,
              expiresAt: event.data.expiresAt,
              userId: event.data.userId,
              displayName: event.data.displayName
            });
          } else if (event.data.type === 'DROPBOX_AUTH_ERROR') {
            console.error(' Authentication error:', event.data.error);
            clearInterval(checkClosed);
            popup?.close();
            window.removeEventListener('message', messageHandler);
            isResolved = true;
            reject(new Error(event.data.error));
          }
        };

        window.addEventListener('message', messageHandler);

        // Add timeout to prevent hanging forever
        setTimeout(() => {
          if (!isResolved) {
            console.error(' Authentication timeout');
            clearInterval(checkClosed);
            popup?.close();
            window.removeEventListener('message', messageHandler);
            isResolved = true;
            reject(new Error('인증 시간이 초과되었습니다. 다시 시도해주세요.'));
          }
        }, 300000); // 5 minutes timeout
      });
    } catch (error) {
      console.error(' Dropbox authenticate error:', error);
      throw handleAPIError(error, 'Dropbox');
    }
  }

  async listFiles(path: string = ''): Promise<CloudFile[]> {
    if (!this.accessToken) {
      throw new AuthenticationError('Dropbox');
    }

    console.log(' Dropbox listFiles called with path:', path, 'token:', this.accessToken ? 'Token exists' : 'No token');

    try {
      const response = await fetch('/api/dropbox/proxy', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          action: 'list_files',
          data: { path },
          accessToken: this.accessToken
        })
      });

      console.log(' Dropbox API response status:', response.status);
      
      if (!response.ok) {
        const errorText = await response.text();
        console.error(' Dropbox API error:', errorText);
        throw new NetworkError('Dropbox', response.status);
      }

      const data = await response.json();
      console.log(' Dropbox API response data:', data);
      console.log(' Total entries found:', data.entries?.length || 0);
      
      if (data.entries) {
        const allFiles = data.entries.filter((entry: DropboxEntry) => entry['.tag'] === 'file');
        const jsonFiles = allFiles.filter((entry: DropboxEntry) => entry.name.endsWith('.json'));
        
        console.log(' All files:', allFiles.map((f: DropboxEntry) => f.name));
        console.log(' JSON files:', jsonFiles.map((f: DropboxEntry) => f.name));
        
        // 폴더도 보여주기 (디버깅용)
        const folders = data.entries.filter((entry: DropboxEntry) => entry['.tag'] === 'folder');
        console.log(' Folders found:', folders.map((f: DropboxEntry) => f.name));
        
        return jsonFiles.map((entry: DropboxEntry) => ({
          id: entry.path_display, // Use path_display as ID for downloads
          name: entry.name,
          path: entry.path_display,
          size: entry.size,
          modifiedAt: entry.client_modified,
          provider: 'dropbox' as StorageProvider
        }));
      }
      
      console.log(' No entries found in response');
      return [];
    } catch (error) {
      console.error(' Dropbox listFiles error:', error);
      throw handleAPIError(error, 'Dropbox');
    }
  }

  async getFile(fileId: string): Promise<string> {
    if (!this.accessToken) {
      throw new AuthenticationError('Dropbox');
    }

    try {
      const response = await fetch('/api/dropbox/proxy', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          action: 'download_file',
          data: { path: fileId },
          accessToken: this.accessToken
        })
      });

      if (!response.ok) {
        throw new NetworkError('Dropbox', response.status);
      }

      const result = await response.json();
      return result.content;
    } catch (error) {
      throw handleAPIError(error, 'Dropbox');
    }
  }

  async saveFile(fileId: string, content: string): Promise<void> {
    if (!this.accessToken) {
      throw new AuthenticationError('Dropbox');
    }

    try {
      const response = await fetch('/api/dropbox/proxy', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          action: 'upload_file',
          data: {
            path: fileId,
            content: content,
            mode: 'overwrite'
          },
          accessToken: this.accessToken
        })
      });

      if (!response.ok) {
        throw new NetworkError('Dropbox', response.status);
      }
    } catch (error) {
      throw handleAPIError(error, 'Dropbox');
    }
  }

  async createFile(name: string, content: string, path: string = ''): Promise<CloudFile> {
    if (!this.accessToken) {
      throw new AuthenticationError('Dropbox');
    }

    try {
      const filePath = path ? `${path}/${name}` : `/${name}`;

      const response = await fetch('/api/dropbox/proxy', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          action: 'upload_file',
          data: {
            path: filePath,
            content: content,
            mode: 'add'
          },
          accessToken: this.accessToken
        })
      });

      if (!response.ok) {
        throw new NetworkError('Dropbox', response.status);
      }

      const data = await response.json();
      return {
        id: data.path_display,
        name: data.name,
        path: data.path_display,
        size: data.size,
        modifiedAt: data.client_modified,
        provider: 'dropbox'
      };
    } catch (error) {
      throw handleAPIError(error, 'Dropbox');
    }
  }

  async deleteFile(fileId: string): Promise<void> {
    if (!this.accessToken) {
      throw new AuthenticationError('Dropbox');
    }

    try {
      const response = await fetch('/api/dropbox/proxy', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          action: 'delete_file',
          data: { path: fileId },
          accessToken: this.accessToken
        })
      });

      if (!response.ok) {
        throw new NetworkError('Dropbox', response.status);
      }
    } catch (error) {
      throw handleAPIError(error, 'Dropbox');
    }
  }
